import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { PictureService } from 'src/app/services/picture.service';
import { Picture } from 'src/app/shared/models/Pictures';


@Component({
  selector: 'app-picture-page',
  templateUrl: './picture-page.component.html',
  styleUrls: ['./picture-page.component.css']
})
export class PicturePageComponent implements OnInit{
  picture: Picture;

  constructor(private route: ActivatedRoute,private router:Router, private pictureService: PictureService ){
    this.picture= new Picture();
  }

  showPicture(){
    this.pictureService.getAll()
    .subscribe((data: Picture) =>{
      id: data.id;
      url: data.url;
      alt: data.alt;
    });
  }


ngOnInit():void{

}}



