import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-picture',
  template: `
    <img [src]="pictureUrl" alt="Picture">
  `
})
export class PictureComponent implements OnInit {
  pictureUrl: string;

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.fetchPicture();
  }

  fetchPicture() {
    // Make an HTTP request to retrieve the picture data
    this.http.get<any>('localhost:8080/v1/api/pictures').subscribe(data => {
      // Assuming the response contains the 'src/original' URL
      this.pictureUrl = data.src.original;
    });
  }
}
