import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { PictureService } from 'src/app/services/picture.service';
import { Picture } from 'src/app/shared/models/Pictures';


@Component({
  selector: 'app-picture-page',
  templateUrl: './picture-page.component.html',
  styleUrls: ['./picture-page.component.css']
})
export class PicturePageComponent implements OnInit{
  pictures!: Picture;

  constructor(private pictureService:PictureService){

  }


ngOnInit(){
  this.pictureService.getAll().subscribe(data =>{
    this.pictures=data;
  });
 }
}



<div class="card my-5">
  <div class="card-body">
    <table class="table table-bordered table-striped">
      <thead class="thead-dark">
        <tr>
          <th scope="col">#</th>
          <th scope="col">Name</th>
          <th scope="col">Email</th>
        </tr>
      </thead>
      <tbody>
        <tr >
          <td>{{ pictures.id }}</td>
          <td>{{ pictures.alt }}</td>
          <td><a href="mailto:{{ pictures.url }}">{{ pictures.url }}</a></td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
