import { Component } from '@angular/core';

@Component({
  selector: 'app-music-page',
  templateUrl: './music-page.component.html',
  styleUrls: ['./music-page.component.css']
})
export class MusicPageComponent {

}
<div class="image-container">
  <div class="center-container">
    <video src="assets/video.mp4"  class="image" autoplay loop controls></video>
    <audio src="/assets/music.mp3" autoplay controls></audio>
    <div class="alt-text"> <p>"Music: Adrian Berengeur: Sun"</p> </div>
</div>
</div>
