import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { sample_activities } from 'src/app/data';
import { Observable } from 'rxjs';
import { Activities } from 'src/app/shared/models/Activities';
import { PICTURES_BY_ID_URL, PICTURES_URL} from 'src/app/shared/models/constants/urls';

@Injectable({
  providedIn: 'root'
})
export class ActivitiesService {

  constructor(private http:HttpClient) { }

  getAll(): Observable<Activities[]>{
    return this.http.get<Activities[]>(PICTURES_URL);
  }


getActivityById(id: number): Observable<Activities>{
  return this.http.get<Activities>(PICTURES_BY_ID_URL + id);
}
}
