import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {


  email: string ="";
  password: string ="";


  constructor(private router: Router,private http: HttpClient) {}



  Login() {
    console.log(this.email);
    console.log(this.password);

    let bodyData = {
      email: this.email,
      password: this.password,
    };

        this.http.post("http://localhost:8085/api/v1/employee/login", bodyData).subscribe(  (resultData: any) => {
        console.log(resultData);

        if (resultData.message == "Email not exits")
        {

          alert("Email not exits");


        }
        else if(resultData.message == "Login Success")

         {
          this.router.navigateByUrl('/home');
        }
        else
        {
          alert("Incorrect Email and Password not match");
        }

      });
    }

}
<div class="container mt-4" >
  <div class="card">
      <h1>Employee Login</h1>
<form>

  <div class="form-group">
      <label>Email</label>
      <input type="email" [(ngModel)]="email" [ngModelOptions]="{standalone: true}" class="form-control" id="email"  placeholder="Enter Email">
    </div>

    <div class="form-group">
      <label>Paaword</label>
      <input type="password" [(ngModel)]="password" [ngModelOptions]="{standalone: true}" class="form-control" id="password" class="form-control" placeholder="Enter Password">
    </div>

   <button type="submit" class="btn btn-primary" (click)="Login()">Login</button>


</form>
</div>

</div>
