import { Component } from '@angular/core';
import { Mood } from '../mood';
import { MOODS } from '../mock-mood';
import { ActivatedRoute,Router } from '@angular/router';
import { MoodServiceService } from '../mood-service.service';
import { MactServiceTsService } from '../services/mact-service.ts.service';

@Component({
  selector: 'app-moods',
  templateUrl: './moods.component.html',
  styleUrls: ['./moods.component.css']
})

export class MoodsComponent{

  moods = MOODS;
  mood!: Mood;
  selectedMood?: Mood;

  onSelect(mood: Mood): void {
    this.selectedMood = mood;
  }

  constructor(private moodService:MoodServiceService,private route:ActivatedRoute,private mactService: MactServiceTsService, private router: Router){

  }

  addToCart(){
    this.mactService.addToCart(this.mood);
    this.router.navigateByUrl('/activity-page');
  }
}
<h2>My Mood</h2>
<ul class="moods">
  <li *ngFor="let mood of moods">
    <a routerLink="/mood/{{mood.id}}">
    <button [class.selected]="mood === selectedMood" type="button" (click)="onSelect(mood)">
      <span class="badge">{{mood.id}}</span>
      <span class="name">{{mood.name}}</span>
    </button>
  </a>
  </li>
</ul>

<div *ngIf="selectedMood">
  <h2>{{selectedMood.name | uppercase}} </h2>

  <div>
    <label for="mood-name">Your mood: </label>
    <input id="mood-name" [(ngModel)]="selectedMood.name" placeholder="name">
  </div>

  <button (click)="addToCart()">Submit</button>

</div>
