import { Component } from '@angular/core';

@Component({
  selector: 'app-title',
  templateUrl: './title.component.html',
  styleUrls: ['./title.component.css']
})
export class TitleComponent {
  constructor(){}
  title!: string;
  margin?='1 rem 0 1 rem 0.2rem';
  fontSize?='1.7rem';

}
<h1 [ngStyle]="{margin:margin, fontSize:fontSize}">
  {{title}}
</h1>
