import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, RouteReuseStrategy } from '@angular/router';
import { Mood } from '../mood';
import { MoodServiceService } from '../mood-service.service';
import { MactServiceTsService } from '../services/mact-service.ts.service';

@Component({
  selector: 'app-mood-page',
  templateUrl: './mood-page.component.html',
  styleUrls: ['./mood-page.component.css']
})
export class MoodPageComponent implements OnInit {
  mood!: Mood;


  constructor(private activatedRoute:ActivatedRoute, private moodService: MoodServiceService,
    private mactService: MactServiceTsService, private router: Router){
activatedRoute.params.subscribe((params)=>{
  if(params['id'])
  this.mood= this.moodService.getMoodById(params['id']);
})

  }
    ngOnInit(): void{
    }

    addToCart(){
      this.mactService.addToCart(this.mood);
    }

}
